<h2 (click)="montokTak()">Products</h2>

<div>
  <div *ngFor="let product of products" class="card">
    <h2>{{ product.name }}</h2>
    <p>{{ product.description }}</p>
    <p>${{ product.price }}</p>
  </div>
</div>

